#include <stdio.h>

static int SERIES_LENGTH = 13;

void printArray(int array[], int length) {
    printf("{");
    for (int i = 0; i < length; i++)
        printf("%d, ", *(array + i));
    printf("\b\b}\n");  // delete 2 chars
}

int main() {
    static char numbers[] = "";

    int circular_buffer[SERIES_LENGTH];
    // fill it up to start
    for (int i = 0; i < SERIES_LENGTH; i++)
        circular_buffer[i] = numbers[i] - '0';

    long int best_ans = 0;
    // now solve
    for (int i = SERIES_LENGTH; i < sizeof(numbers) / sizeof(char); i++) {
        long int product = 1;
        for (int i = 0; i < SERIES_LENGTH; i++)
            product *= circular_buffer[i];
        if (product > best_ans)
            best_ans = product;

        // write over the oldest number
        circular_buffer[i % SERIES_LENGTH] = numbers[i] - '0';
    }
    printf("%ld\n", best_ans);
    return 0;
}


